{"metadata":{"kernelspec":{"language":"python","display_name":"Python 3","name":"python3"},"language_info":{"name":"python","version":"3.10.12","mimetype":"text/x-python","codemirror_mode":{"name":"ipython","version":3},"pygments_lexer":"ipython3","nbconvert_exporter":"python","file_extension":".py"},"kaggle":{"accelerator":"none","dataSources":[],"dockerImageVersionId":30822,"isInternetEnabled":false,"language":"python","sourceType":"notebook","isGpuEnabled":false}},"nbformat_minor":4,"nbformat":4,"cells":[{"cell_type":"code","source":"import pandas as pd\nimport matplotlib.pyplot as plt\nimport seaborn as sns\n\n# Generate a sample dataset for customer segmentation\ndata = pd.DataFrame({\n    \"CustomerID\": range(1, 21),\n    \"Age\": [25, 34, 28, 42, 35, 29, 41, 23, 30, 40, 50, 36, 22, 27, 38, 32, 45, 33, 26, 39],\n    \"Annual_Income\": [40000, 52000, 35000, 72000, 58000, 39000, 81000, 30000, 45000, 70000, 85000, 60000, 28000, 32000, 64000, 50000, 75000, 51000, 42000, 67000],\n    \"Spending_Score\": [60, 80, 50, 90, 70, 55, 85, 40, 65, 75, 95, 68, 35, 48, 78, 62, 88, 58, 54, 82]\n})\n\n# Save dataset as a CSV (optional)\ndata.to_csv(\"customer_data.csv\", index=False)\n\n# Preview the dataset\nprint(\"Customer dataset preview:\")\nprint(data.head())\n\n# Scatter plot: Spending Score vs. Annual Income\nplt.figure(figsize=(10, 6))\nsns.scatterplot(data=data, x='Annual_Income', y='Spending_Score', hue='Age', palette='cool', size='Age', sizes=(50, 200))\nplt.title('Spending Score vs. Annual Income')\nplt.xlabel('Annual Income ($)')\nplt.ylabel('Spending Score')\nplt.legend(title='Age', bbox_to_anchor=(1.05, 1), loc='upper left')\nplt.grid(True)\nplt.show()\n\n# Box plot: Age vs. Spending Score\nplt.figure(figsize=(8, 6))\nsns.boxplot(data=data, x='Spending_Score', y='Age', palette='Set2')\nplt.title('Age Distribution by Spending Score')\nplt.xlabel('Spending Score')\nplt.ylabel('Age')\nplt.grid(True)\nplt.show()\n\n# Correlation heatmap\nplt.figure(figsize=(6, 4))\ncorr = data[['Age', 'Annual_Income', 'Spending_Score']].corr()\nsns.heatmap(corr, annot=True, cmap='coolwarm', fmt=\".2f\")\nplt.title('Correlation Heatmap')\nplt.show()","metadata":{"_uuid":"8f2839f25d086af736a60e9eeb907d3b93b6e0e5","_cell_guid":"b1076dfc-b9ad-4769-8c92-a6c4dae69d19","trusted":true,"execution":{"iopub.status.busy":"2024-12-20T16:07:21.683148Z","iopub.execute_input":"2024-12-20T16:07:21.684307Z","iopub.status.idle":"2024-12-20T16:07:26.338465Z","shell.execute_reply.started":"2024-12-20T16:07:21.684216Z","shell.execute_reply":"2024-12-20T16:07:26.337369Z"}},"outputs":[],"execution_count":null}]}
